import { KeyValuePair } from '@maxbcm/bcm-foundation';
export const baseUrl = () => {
    return window.location.protocol + "//" + window.location.host;
};
export const GlobalDefinition = Object.freeze({
    RESPONSE_SOURCE_HEADER: new KeyValuePair('BCM_internal', 'X-Response-Source'),
    POTATO: 'ptto'
});
export function reloadWindow() {
    var currentUrl = window.location.href;
    var url = new URL(currentUrl);
    const urlParams = new URLSearchParams(window.location.search);
    urlParams.delete(GlobalDefinition.POTATO);
    let sanitizedUrl;
    if (urlParams.size > 0) {
        sanitizedUrl = url.origin + url.pathname + '?' + urlParams.toString();
    }
    else {
        sanitizedUrl = url.origin + url.pathname;
    }
    window.location.replace(sanitizedUrl);
}
