import './Modal.css';
import closeIconImg from './Resources/IconClose.png';
export class Modal {
    constructor(container, embed) {
        // Layout the view
        const modal = document.createElement('div');
        modal.classList.add('modalBox');
        modal.appendChild(embed);
        const closeIcon = document.createElement('img');
        closeIcon.classList.add('closeIconImage');
        closeIcon.src = closeIconImg;
        const closeIconButton = document.createElement('div');
        closeIconButton.classList.add('closeIconButton');
        closeIconButton.appendChild(closeIcon);
        const modalContainer = document.createElement('div');
        modalContainer.classList.add('modalContainer');
        modalContainer.appendChild(modal);
        modalContainer.appendChild(closeIconButton);
        const overlay = document.createElement('div');
        overlay.classList.add('modalOverlay');
        container.appendChild(overlay);
        container.appendChild(modalContainer);
        // Configure the buttons
        closeIconButton.addEventListener('click', () => {
            overlay.remove();
            modalContainer.remove();
        });
        modalContainer.addEventListener('click', (event) => {
            overlay.remove();
            modalContainer.remove();
        });
        modal.addEventListener('click', (event) => {
            event.stopPropagation();
        });
    }
}
