import './UserStatusBadge.css';
import loggedInImg from './Resources/IconUserLoggedIn.png';
import loggedOutImg from './Resources/IconUserLoggedOut.png';
export class UserStatusBadge {
    element;
    userIcon;
    constructor() {
        const userIcon = document.createElement('img');
        userIcon.classList.add('userStatusIcon');
        userIcon.src = loggedOutImg;
        const userIconDiv = document.createElement('div');
        userIconDiv.classList.add('userIconContainer');
        userIconDiv.appendChild(userIcon);
        this.userIcon = userIcon;
        this.element = userIconDiv;
    }
    updateState(loggedIn) {
        if (loggedIn) {
            this.userIcon.src = loggedInImg;
        }
        else {
            this.userIcon.src = loggedOutImg;
        }
    }
}
